import React from 'react';
import PropTypes from 'prop-types';
import APIService from './APIService';
import { useCookies } from 'react-cookie';

function ArticleItem({ article, editBtn, deleteBtn }) {
    return (
        <div key={article.id}>
            <h1>{article.title}</h1>
            <p>{article.description}</p>
            <div className="row">
                <div className="col-md-1">
                    <button
                        className="btn btn-primary"
                        onClick={() => editBtn(article)}
                        aria-label={`Update ${article.title}`}
                    >
                        Update
                    </button>
                </div>
                <div className="col-md-1">
                    <button
                        className="btn btn-danger"
                        onClick={() => deleteBtn(article)}
                        aria-label={`Delete ${article.title}`}
                    >
                        Delete
                    </button>
                </div>
            </div>
            <hr className="post_line" />
        </div>
    );
}

function ArticleList(props) {
    const [token] = useCookies(['mytoken']);
    const articles = props.articles ?? [];

    const editBtn = (article) => {
        props.editBtn(article);
    };

    const deleteBtn = (article) => {
        APIService.DeleteArticle(article.id, token['mytoken'])
            .then(() => props.deleteBtn(article))
            .catch(error => {
                if (error.response && error.response.status === 401) {
                    console.error("Token expired or invalid. Please log in again.");
                    window.location.href = "/login";
                } else {
                    console.error("Makale silinemedi:", error);
                }
            });
    };

    return (
        <div>
            {articles.map(article => (
                <ArticleItem
                    key={article.id}
                    article={article}
                    editBtn={editBtn}
                    deleteBtn={deleteBtn}
                />
            ))}
        </div>
    );
}

ArticleList.propTypes = {
    articles: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.number.isRequired,
            title: PropTypes.string.isRequired,
            description: PropTypes.string.isRequired,
        })
    ).isRequired,
    editBtn: PropTypes.func.isRequired,
    deleteBtn: PropTypes.func.isRequired,
};

ArticleList.defaultProps = {
    articles: [],
};

export default ArticleList;
