import React from 'react';
import PropTypes from 'prop-types';
import APIService from './APIService';
import { useCookies } from 'react-cookie';

function ArticleList(props) {
    const [token] = useCookies(['mytoken']);

    const editBtn = (article) => {
        props.editBtn(article);
    };

    const deleteBtn = (article) => {
        APIService.DeleteArticle(article.id, token['mytoken'])
            .then(() => props.deleteBtn(article))
            .catch(error => {
                if (error.message.includes("401")) {
                    console.error("Token expired or invalid. Please log in again.");
                    window.location.href = "/login";
                } else {
                    console.error("Makale silinemedi:", error);
                }
            });
    };

    const articles = Array.isArray(props.articles) ? props.articles : [];

    return (
        <div>
            {articles.map(article => (
                <div key={article.id}>
                    <h1>{article.title}</h1>
                    <p>{article.description}</p>
                    <div className="row">
                        <div className="col-md-1">
                            <button className="btn btn-primary" onClick={() => editBtn(article)}>Update</button>
                        </div>
                        <div className="col-md-1">
                            <button className="btn btn-danger" onClick={() => deleteBtn(article)}>Delete</button>
                        </div>
                    </div>
                    <hr className="post_line" />
                </div>
            ))}
        </div>
    );
}

ArticleList.propTypes = {
    articles: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.number.isRequired,
            title: PropTypes.string.isRequired,
            description: PropTypes.string.isRequired,
        })
    ).isRequired,
    editBtn: PropTypes.func.isRequired,
    deleteBtn: PropTypes.func.isRequired,
};

ArticleList.defaultProps = {
    articles: [],
};

export default ArticleList;
