import React, { useState } from 'react';
import { useCookies } from 'react-cookie';
import { Modal, Button, Form } from 'react-bootstrap';
import APIService from './APIService';

function NavBar() {
    const [token, setToken, removeToken] = useCookies(['mytoken']);
    const [showModal, setShowModal] = useState(false);
    const [isLogin, setIsLogin] = useState(true);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const logoutBtn = () => removeToken(['mytoken']);
    const handleClose = () => setShowModal(false);
    const handleShow = () => setShowModal(true);

    const loginBtn = () => {
        if (username.trim() && password.trim()) {
            APIService.loginUser({ username, password })
                .then(resp => {
                    setToken('mytoken', resp.token);
                    handleClose();
                })
                .catch(error => console.error(error));
        }
    };

    const registerBtn = () => {
        if (username.trim() && password.trim()) {
            APIService.registerUser({ username, password })
                .then(() => loginBtn())
                .catch(error => console.error(error));
        }
    };

    return (
        <>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <a className="navbar-brand" href="/">Learn Python Blog</a>
                    <div className="collapse navbar-collapse">
                        <ul className="navbar-nav ms-auto">
                            {token['mytoken'] ? (
                                <li className="nav-item">
                                    <button className="btn btn-link nav-link" onClick={logoutBtn}>Log out</button>
                                </li>
                            ) : (
                                <li className="nav-item">
                                    <button className="btn btn-link nav-link" onClick={handleShow}>Login/Register</button>
                                </li>
                            )}
                        </ul>
                    </div>
                </div>
            </nav>

            <Modal show={showModal} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{isLogin ? 'Login' : 'Register'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>Username</Form.Label>
                            <Form.Control type="text" value={username} onChange={e => setUsername(e.target.value)} />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" value={password} onChange={e => setPassword(e.target.value)} />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={isLogin ? loginBtn : registerBtn}>{isLogin ? 'Login' : 'Register'}</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default NavBar;
