export default class APIService {
    static async updateArticle(article_id, body, token) {
        try {
            const response = await fetch(`http://127.0.0.1:8000/api/articles/${article_id}/`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}` // Bearer token kullanımı
                },
                body: JSON.stringify(body)
            });
            return await response.json();
        } catch (error) {
            console.error("Makale güncellenemedi:", error);
            throw error; // Hatayı tekrar fırlatarak bileşende yakalayabilirsiniz
        }
    }

    static async insertArticle(body, token) {
        try {
            const response = await fetch(`http://127.0.0.1:8000/api/articles/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}` // Bearer token kullanımı
                },
                body: JSON.stringify(body)
            });
            return await response.json();
        } catch (error) {
            console.error("Makale eklenemedi:", error);
            throw error;
        }
    }

    static async deleteArticle(article_id, token) {
        try {
            const response = await fetch(`http://127.0.0.1:8000/api/articles/${article_id}/`, {
                method: 'DELETE', // DELETE metodu kullanımı
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}` // Bearer token kullanımı
                }
            });
            return await response.json();
        } catch (error) {
            console.error("Makale silinemedi:", error);
            throw error;
        }
    }

    static async loginUser(body) {
        try {
            const response = await fetch(`http://127.0.0.1:8000/api/token/`, { // Simple JWT token endpoint'i
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(body)
            });
            return await response.json();
        } catch (error) {
            console.error("Giriş yapılamadı:", error);
            throw error;
        }
    }

    static async registerUser(body) {
        try {
            const response = await fetch(`http://127.0.0.1:8000/api/users/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(body)
            });
            return await response.json();
        } catch (error) {
            console.error("Kayıt yapılamadı:", error);
            throw error;
        }
    }
}
