import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCookies } from 'react-cookie';
import APIService from './APIService';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useCookies(['mytoken']);
    const [isLogin, setLogin] = useState(true);
    let navigate = useNavigate();

    useEffect(() => {
        const user_token = token['mytoken'];
        if (typeof user_token === 'undefined') {
            navigate('/');
        } else {
            navigate('/articles');
        }
    }, [token, navigate]);

    const loginBtn = () => {
        if (username.trim() && password.trim()) {
            APIService.loginUser({ username, password })
                .then(resp => setToken('mytoken', resp.token))
                .catch(error => console.error(error));
        } else {
            console.log('Username and password are required');
        }
    };

    const registerBtn = () => {
        if (username.trim() && password.trim()) {
            APIService.registerUser({ username, password })
                .then(() => loginBtn())
                .catch(error => console.error(error));
        } else {
            console.log('Username and password are required');
        }
    };

    return (
        <div className="container">
            <h1 className="alert alert-danger">Learn Python Blog</h1>
            <div className="col-sm-4">
                {isLogin ? <h3>Please Login Here</h3> : <h3>Please Register Here</h3>}
                <div className="form-group">
                    <label>Username</label>
                    <input type="text" className="form-control" value={username} onChange={e => setUsername(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" value={password} onChange={e => setPassword(e.target.value)} />
                </div>
                <br />
                {isLogin ? (
                    <>
                        <button onClick={loginBtn} className="btn btn-primary">Login</button>
                        <p>If you don't have an account, <button className="btn btn-link" onClick={() => setLogin(false)}>Register</button></p>
                    </>
                ) : (
                    <>
                        <button onClick={registerBtn} className="btn btn-primary">Register</button>
                        <p>Already have an account? <button className="btn btn-link" onClick={() => setLogin(true)}>Login</button></p>
                    </>
                )}
            </div>
        </div>
    );
}

export default Login;
