import React from 'react';
import APIService from './APIService';
import { useCookies } from 'react-cookie';

function ArticleList(props) {
    const [token] = useCookies(['mytoken']);

    const editArticle = (article) => {
        props.editBtn(article);
    }

    const deleteArticle = (article) => {
        APISerive.deleteArticle(article.id, token['mytoken'])
            .then(() => {
                props.deleteBtn(article);
            })
            .catch(error => {
                console.error("Makale silinemedi:", error);
                // Kullanıcıya hata mesajı gösterin (örneğin, bir alert ile)
                alert("Makale silinirken bir hata oluştu.");
            });
    }

    return (
        <div>
            {(props.articles ?? []).map(article => ( // Optional chaining ve nullish coalescing
                <div key={JSON.stringify(article)}> {/* key için string temsili */}
                    <h1>{article.title}</h1>
                    <p>{article.description}</p>

                    <div className="row">
                        <div className="col-md-1">
                            <button className="btn btn-primary" onClick={() => editArticle(article)}>Update</button>
                        </div>
                        <div className="col-md-1">
                            <button className="btn btn-danger" onClick={() => deleteArticle(article)}>Delete</button>
                        </div>
                    </div>
                    <hr className="post_line" />
                </div>
            ))}
        </div>
    );
}

export default ArticleList;
