class APIService {
    static BASE_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000';

    static async updateArticle(article_id, body, token) {
        const response = await fetch(`${this.BASE_URL}/api/articles/${article_id}/`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
            },
            body: JSON.stringify(body),
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return await response.json();
    }

    static async insertArticle(body, token) {
        const response = await fetch(`${this.BASE_URL}/api/articles/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
            },
            body: JSON.stringify(body),
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return await response.json();
    }

    static async deleteArticle(article_id, token) {
        const response = await fetch(`${this.BASE_URL}/api/articles/${article_id}/`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`,
            },
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
    }

    static async loginUser(body) {
        const response = await fetch(`${this.BASE_URL}/api/token/`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(body),
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return await response.json();
    }

    static async registerUser(body) {
        const response = await fetch(`${this.BASE_URL}/api/users/`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(body),
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return await response.json();
    }
}

export default APIService;
